package javabasic;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numero= 1;
		while (numero <= 100) {
			System.out.println(numero);
		
		//o tambien puede ser numero++;
		if (numero==34) {
			break;
		}
		numero = numero +1;
				}
		for (int num=1; num<=100; num++) {
			System.out.println(num);
		}
		
		
		
		
		
	} // cierra main

} // cierr clase
